      if (endDate) filters.endDate = new Date(endDate as string);

      const transactions = await storage.getTransactions(filters);
      const bankAccounts = await storage.getBankAccounts();

      const report = bankAccounts.map(account => {
        const accountTransactions = transactions.filter(t => 
          t.fromBankAccountId === account.id || t.toBankAccountId === account.id
        );
        const income = accountTransactions
          .filter(t => t.type === "income" && t.fromBankAccountId === account.id)
          .reduce((sum, t) => sum + parseFloat(t.convertedInrAmount), 0);
        const expense = accountTransactions
          .filter(t => t.type === "expense" && t.fromBankAccountId === account.id)
          .reduce((sum, t) => sum + parseFloat(t.convertedInrAmount), 0);
        const transferIn = accountTransactions
          .filter(t => t.type === "transfer" && t.toBankAccountId === account.id)
          .reduce((sum, t) => sum + parseFloat(t.convertedInrAmount), 0);
        const transferOut = accountTransactions
          .filter(t => t.type === "transfer" && t.fromBankAccountId === account.id)
